import React from 'react';
import {ChildGizmo} from './childGizmo';

// const ListItem = ({ value }) => (
//   <li>{value}</li>
// );

// const List = ({ items }) => (
//   <ul>
//     {
//       items.map((item, i) => <ListItem key={i} value={item} />)
//     }
//   </ul>
// );

export class ParentGizmo extends React.Component {
  constructor(props) {
    super(props);
    this.myRef = React.createRef();
    this.state = {
      backgroundColor: '#fff',
      width: '200px',
      height: '100px',
      borderRadius: '0px',
      componentEvents: [],
      latestComponentEvent: ''
    };
    this.toggleColor = this.toggleColor.bind(this);
    this.toggleHeight = this.toggleHeight.bind(this);
    this.toggleWidth = this.toggleWidth.bind(this);
    this.toggleCorners = this.toggleCorners.bind(this);
    this.componentLifeCycleEvent = this.componentLifeCycleEvent.bind(this);
}

toggleColor() {	
	this.setState({
		backgroundColor: this.state.backgroundColor === '#fff' ? '#000' : '#fff'			
	});
}

toggleHeight() {
	this.setState({
		height: this.state.height === '100px' ? '200px' : '100px'
	});
}

toggleWidth() {
	this.setState({
		width: this.state.width === '200px' ? '300px' : '200px'
	});
}

toggleCorners() {
	this.setState({
		borderRadius: this.state.borderRadius === '0px' ? '50px' : '0px'
	});
}

componentLifeCycleEvent(lifeCycleEvent) {
  console.log('componentLifeCycleEvent called');
  var node = document.createElement("LI");
  var textnode = document.createTextNode(lifeCycleEvent);
  node.appendChild(textnode);

const value = this.myRef.current;
console.log(value);
//this.myRef.current.focus();

  this.myRef.current.innerHTML += node.outerHTML;

}

componentWillMount() {
	//this.componentLifeCycleEvent('componentWillMount called');

}

componentDidMount() {
  this.componentLifeCycleEvent('componentDidMount called');
}

componentDidUpdate(prevProps, prevState) {
	this.componentLifeCycleEvent('componentDidUpdate called');
}

render() {
	return (
		<div>
			<div style={{
				backgroundColor: this.state.backgroundColor,
				width: this.state.width,
				height: this.state.height,
				borderRadius: this.state.borderRadius
				}}>
			</div>
			<ul ref={this.myRef} className='gizmo-details' />
			<ChildGizmo 
				onChangeColor={this.toggleColor}
				onChangeHeight={this.toggleHeight}
				onChangeWidth={this.toggleWidth}
				onChangeCorners={this.toggleCorners}
			/>
		</div>
		)
	}
}
