                	</div>
                	<div className='gizmo-button-detail' />
                </div>
                 <div className='gizmo-button-frame-container'>
                	<div className='gizmo-button gizmo-button-frame' onMouseDown={gizmoButtonMouseDown} onMouseUp={gizmoButtonMouseUp} onMouseOut={gizmoButtonMouseUp} onClick={this.newFrame}>
                	  <i className="fas fa-cogs"></i>
                	</div>
                	<div className='gizmo-button-detail' />
                </div>
                 <div className='gizmo-button-windows-container'>
                	<div className='gizmo-button gizmo-button-windows' onMouseDown={gizmoButtonMouseDown} onMouseUp={gizmoButtonMouseUp} onMouseOut={gizmoButtonMouseUp}>
                	  <i className="fas fa-border-all"></i>
                	</div>
                	<div className='gizmo-button-detail' />
                </div>
                <div className='gizmo-button-decals-container'>
                	<div className='gizmo-button gizmo-button-decals' onMouseDown={gizmoButtonMouseDown} onMouseUp={gizmoButtonMouseUp}  onMouseOut={gizmoButtonMouseUp}>
