                	</div>
                	<div className='gizmo-button-detail' />
                </div>
                 <div className='gizmo-button-build-container'>
                	<div className='gizmo-button-large gizmo-button-build' onMouseDown={gizmoButtonMouseDown} onMouseUp={gizmoButtonMouseUp}  onMouseOut={gizmoButtonMouseUp} onClick={this.convertMonster}>
                	  <i className="fas fa-bold"></i>
                	</div>
