import React from 'react';

const updateMonitor = (updateText) => {

	for (var i = updateText.length - 1; i >= 0; i--) {
		setTimeout(function() {
			document.getElementById('gizmo-updates-screen').prepend(updateText.charAt(i));
		}, 100)

	}

}

export default updateMonitor;
