import React from 'react';
import {Gizmo} from './gizmo';
import {Crane} from './crane';
import './gizmo.css';

export class GizmoSection extends React.Component {
  constructor(props) {
	super(props);
	this.convertMonster = this.convertMonster.bind(this);
	this.pickUpMonster = this.pickUpMonster.bind(this);
	this.removeMonster = this.removeMonster.bind(this);
  }
  state = {
    gizmoCount : 0,
    gizmoArray : [],
    animationQueue: [],
    animationQueuePriority: 0,
    currentAnimation: []
  }

	createGizmo = () => {
		this.setState({
			gizmoCount : this.state.gizmoCount + 1,
			gizmoArray: [...this.state.gizmoArray, <Gizmo key={this.state.gizmoCount} number={this.state.gizmoCount} onFilterOut={this.convertMonster} />]			
		});
	}

	convertMonster(monsterNumber) {
		console.log('monsterNumber: ' + monsterNumber);
		//this.craneIn(monsterNumber);
		this.setState({
			animationQueue: [
			  {
			  	a,
			  	priority: 1
			  },
			  {
			  	b,
			  	priority: 2
			  },
			  {
			  	c,
			  	priority: 3
			  },
			  {
			  	d,
			  	priority: 3
			  }
			],
			currentAnimation: []
		});
	}

	craneIn(monsterNumber) {
		console.log('craneIn');

		const element = document.getElementById('crane');

		// element.addEventListener('animationend', () => {
	 //      element.classList.add('crane-move-left');
	 //    });
		element.classList.add('crane-move-right');
	}

	pickUpMonster(monsterNumber) {
		//const element = document.getElementById('crane');
		//console.log(this.state.gizmoArray[monsterNumber]);
		//console.log(this.gizmoRef.current);
	}

	removeMonster(monsterNumber) {
		console.log('removeMonster');

		// run some animation here, wait till its done, and then fire set state like below.

		this.setState({
			gizmoArray : this.state.gizmoArray.filter(function(item) {
				return item.props.number !== monsterNumber
			})
		});
	}

	componentDidUpdate(prevProps, prevState) {
		if (prevState.animationQueue !== this.state.animationQueue) { // We have an animation Queue!
			while(this.state.animationQueuePriority <= this.state.animationQueue.length) { 

			let currentPriority = this.state.animationQueuePriority;

			this.state.animationQueue.forEach(function(animation, index) { // Loop through every animation in the queue
				if (animation.priority === currentPriority) {
					this.setState({
						currentAnimation: [...this.state.currentAnimation, animation]
					});
				}
				else {
					this.setState({
						animationQueuePriority: this.state.animationQueuePriority + 1
					});
				}

				// for (let i = 0; i <= this.state.animationQueue.length; i++) {
				// //console.log(this.state.animationQueue[i].priority);
				// 	if (this.state.animationQueue[i].priority === i) {
				// 		this.setState({
				// 		  currentAnimation: [...this.state.currentAnimation, this.state.animationQueue[i]]
				// 		});
				// 	}
				// }
				});
			}
		}
    }

	render() {
		return (
		  <div className='gizmos-container'>
		  	<Crane />
		    <section className="gizmo-button-container">
		      <GizmoButton onClick={this.createGizmo} />
		    </section>
			  <section className="gizmo-creater">
				{this.state.gizmoArray}
			</section>
            <section className="gizmo-converter">
             <p>need to design a converter</p>
            </section>
            <section className="gizmo-result">
             <p>need to design a gizmo result</p>
            </section>
	      </div>
		)
	}
}


