import React from 'react';
import {SVGFrame} from './svgFrame';
import './frame.css';
import frame_ice_cream from '../../images/frame-ice-cream.svg';

export class Frame extends React.Component {
	constructor(props) {
		super(props);
	}
	state = {	   		
		frameTypes: [
		    {
			  frameTypeName: 'ice-cream-truck'
		    },
		    {
			  frameTypeName: 'fire-truck'
		    },
		    {
			  frameTypeName: 'school-bus'
		    },
		    {
			  frameTypeName: 'tractor'
		    },
		    {
			  frameTypeName: 'police-car'
		    },
		    {
			  frameTypeName: 'garbage-truck'
		    }
   		],
   		frameOptions: [
	   		{
	   			frameOptionName: 'default'
	   		},
	   		{
	   		    frameOptionName: 'cool'
	   		},
	   		{
	   			frameOptionName: 'hot'
	   		},
	   		{
	   			frameOptionName: 'eco'
	   		},
	   		{
	   			frameOptionName: 'classic'
	   		},
	   		{
	   			frameOptionName: 'sleek'
	   		},
	   		{
	   			frameOptionName: 'rusty'
	   		}
   		]

	}

	componentDidMount() {
	}

	componentWillUpdate(prevProps, prevState) {	
		console.log(prevProps);	
	}

	componentWillUnmount() {
		this.props.cycleFrameType(this.state.frameTypes.length);
		// if (this.state.currentFrameType === this.state.frameTypes.length) {
		// 	console.log('here');
		// 		this.setState({
		// 			currentFrameType: 0
		// 		})
		// 	} else {
		// 		console.log('ok here');
		// 		this.setState({
		// 			currentFrameType: this.state.currentFrameType + 1
		// 		})
		// 	}
	}

	render() {
		return (
		  <SVGFrame
            id='frame'
            frameType={this.state.frameTypes[this.props.currentFrameType]}
            frameOption={this.state.frameOptions[this.props.currentFrameOption]}            
		  />
		)
	}
}
