import React from 'react';

export class Stripe extends React.Component {


constructor() {
	super();
	this.state = {
		opacity: 0,
		scale: 1
	}
}

onHide() {
	this.setState({
		opacity: 1
	});
}	

onScale() {
	this.setState({
		scale: this.state.scale > 1 ? 1 : 1.5
	});
}

render() {
	return (
		<div className={'header-stripe, ${this.props.className}'}> 
	    </div>
	 );
	}
}
