import React from 'react';

export class GizmoControls extends React.Component {

	componentDidUpdate() {
		alert('Gizmo Controls Updated');
	}

	render() {
	return(
	<div className='gizmo-controls'>
		<input type="range" min="100" max="100000" val="1000" step="1000" ></input>
		<button onClick={this.props.onChangeWidth}>Toggle width</button>
		<button onClick={this.props.onChangeColor}>Change Color</button>
		<button className='gizmo-control-convert-button' onClick={this.props.onBuildMonster} disabled={this.props.disabled}>Convertr me!</button>
	</div>
	)	
	}
}
