import React from 'react';

export class Monster extends React.Component {

	constructor(props) {
		super(props);
		this.monsterRef = React.createRef();
	}

	componentWillMount() {
		console.log('componentDidMount');
		this.monsterRef.current.addEventListener('transitionend', function() {
			//This is for when the transition has ended...
			alert('im gone now');
			//this.moveOver(this.monsterRef.current, 'margin-left', '100%');
		});
		//
	}

	componentDidMount() {
		console.log('componentDidMount');
		this.moveOver(this.monsterRef.current, 'margin-left', '100%');
	}

	moveOver(element, property, value) {
		console.log('moveOver');
		element.style[property] = value;
	}

	render() {
		return (
			<div ref={this.monsterRef} className={`monster ${this.props.species}`}>
				<div className='head' />
				<div className='torso' />
				<div className='left-arm' />
				<div className='right-arm' />
				<div className='left-leg' />
				<div className='right-leg' />
			</div>
		)
	}

}
