import React from 'react';
import {SVGFrame} from './svgFrame';
import './frame.css';
import frame_ice_cream from '../../images/frame-ice-cream.svg';

export class Frame extends React.Component {
	constructor(props) {
		super(props);
	}
	state = {	
	    currentFrameType: 0,    		
		frameTypes: [
		    {
			  frameTypeName: 'ice-cream-truck'
		    },
		    {
			  frameTypeName: 'fire-truck'
		    },
		    {
			  frameTypeName: 'school-bus'
		    },
		    {
			  frameTypeName: 'tractor'
		    },
		    {
			  frameTypeName: 'police-car'
		    },
		    {
			  frameTypeName: 'garbage-truck'
		    }
   		],
   		currentFrameOption: 0,
   		frameOptions: [
	   		{
	   		  frameOptionName: 'cool'
	   		},
	   		{
	   			frameOptionName: 'hot'
	   		},
	   		{
	   			frameOptionName: 'eco'
	   		},
	   		{
	   			frameOptionName: 'classic'
	   		},
	   		{
	   			frameOptionName: 'sleek'
	   		},
	   		{
	   			frameOptionName: 'rusty'
	   		}
   		]

	}

	componentDidMount() {
	}

	componentWillUpdate(prevProps, prevState) {		
	}

	componentWillUnmount() {
		if (this.state.currentFrameType === this.state.frameTypes.length) {
				this.setState({
					currentFrameOption: 0
				})
			} else {
				this.setState({
					currentFrameOption: this.state.currentFrameOption + 1
				})
			}
	}

	render() {
		return (
		  <SVGFrame
            id='frame'
            frameType={this.state.frameTypes[this.state.currentFrameType]}
            frameOption={this.state.frameOptions[this.state.currentFrameOption]}            
		  />
		)
	}
}
