		console.log('setup');
		this.monsterRef.current.addEventListener('transitionend', function() {
			//This is for when the transition has ended...
			this.props.oncreateFloatingMonster(this.state, this.props);
		});
	  // Calls the function with any required arguments
	  return fn(this.monsterRef.current)
	}

