import React from 'react';

const updateMonitor = (updateText, callback) => {

	for (var i = updateText.length - 1; i >= 0; i--) {
		// setTimeout(function() {
		// 	console.log('here');
		// }, 2000)
		setTimeout(() => callback(updateText.charAt(i)), 15); 
	}

	document.getElementById('gizmo-updates-screen').prepend();

}

export default updateMonitor;
