import React from 'react';
import {Gizmo} from './gizmo';
import {Frame} from './frame';

export class Vehicle extends React.Component {
	constructor(props) {
	  super(props);

	  this.state = {
	    displayFrame: false,
	    currentFrameType: 0,
	    currentFrameOption: 0,
	    frameTypes: [
		    {
			  frameTypeName: 'ice-cream-truck'
		    },
		    {
			  frameTypeName: 'fire-truck'
		    },
		    {
			  frameTypeName: 'school-bus'
		    },
		    {
			  frameTypeName: 'tractor'
		    },
		    {
			  frameTypeName: 'police-car'
		    },
		    {
			  frameTypeName: 'garbage-truck'
		    }
   		],
   		frameOptions: [
	   		{
	   			frameOptionName: 'default'
	   		},
	   		{
	   		    frameOptionName: 'cool'
	   		},
	   		{
	   			frameOptionName: 'hot'
	   		},
	   		{
	   			frameOptionName: 'eco'
	   		},
	   		{
	   			frameOptionName: 'classic'
	   		},
	   		{
	   			frameOptionName: 'sleek'
	   		},
	   		{
	   			frameOptionName: 'rusty'
	   		}
   		]
	  }	  
	}

	componentWillUpdate(prevProps, prevState) {	
		//Bring in frame
		if (this.props.displayFrame && this.state.displayFrame === false) {						
			this.setState({
				displayFrame: true
			})
		}

		//Remove frame
		if (!this.props.displayFrame && this.state.displayFrame === true) {						
			this.setState({
				displayFrame: false
			})
		}

		//Cycle frame option
		if (this.props.currentFrameOption !== this.state.currentFrameOption) {
			this.setState({
				currentFrameOption: this.props.currentFrameOption
			})
		}
	}

	componentWillUnmount() {			
		this.props.newVehicle();
		this.setState({
			displayFrame: false,
			currentFrameOption: 0
		});
  	}

	cycleFrameType = (frameTypeCount) => {
		let nextFrame= this.state.currentFrameType;
		//if (this.state.currentFrameType <= frameTypeCount) {
			nextFrame++;
		//}
		// else {
		// 	nextFrame = 0;
		// }
		this.setState({
			currentFrameType: nextFrame		
		});
	}	

	resetFrameOption = () => {
		this.setState({

		});
	}

	render() {

		let frame;


		if (this.state.displayFrame) {
			frame = <Frame currentFrameType={this.state.currentFrameType} cycleFrameType={this.cycleFrameType} currentFrameOption={this.state.currentFrameOption} />
		}

		return (
			<div className='vehicle' id={'vehicleId' + this.props.currentVehicleId}>
				<Gizmo onCreateMonster={this.createMonster} currentFrameType={this.props.currentFrameType} />				
				{frame}
			</div>
		)
	}
}
