import React from 'react';
import {NewMonsterPartsButton} from './newMonsterPartsButton';
import {Gizmo} from './gizmo';
import {Crane} from './crane';
import {Monster} from './monster';
import {FloatingMonster} from './floatingMonster';
import './gizmo.css';
import './monster.css';
import './floatingMonster.css';

export class GizmoSection extends React.Component {
  constructor(props) {
	super(props);
	this.convertMonster = this.convertMonster.bind(this);
	this.removeComponent = this.removeComponent.bind(this);
	this.createMonster = this.createMonster.bind(this);
	this.createFloatingMonster = this.createFloatingMonster.bind(this);
  }
  state = {
    gizmoCount: 0,
    gizmoArray: [],
    animationQueue: [],
    animationQueuePriority: 0,
    currentAnimation: [],
    monsterCount: 0,
    monsterArray: [],
    floatingMonsterCount: 0,
    floatingMonsterArray: [],
  }

	createGizmo = () => {
		this.setState({
			gizmoCount : this.state.gizmoCount + 1,
			gizmoArray: [...this.state.gizmoArray, 
			  <Gizmo 
			    key={this.state.gizmoCount} 
			    number={this.state.gizmoCount} 
			    onFilterOut={this.convertMonster} 
			    onCreateMonster={this.createMonster}
			  />
			]
		});
	}

	convertMonster(monsterNumber, monsterOffsetTop) {
		document.getElementsByClassName('gizmo-control-convert-button').disabled = true;
		this.setState({	
		craneAnimating: true,
		animationQueue: [
		  {
		  	animationName: 'craneToPickup',
		  	animationSequence: craneToPickup,
		  	priority: 1
		  },
		  {
		  	animationName: 'craneClawLower',
		  	animationSequence: craneClawLower,
		  	priority: 2,
		  	monsterOffsetTop: monsterOffsetTop
		  },
		  {
		  	animationName: 'craneClawGrab',
		  	animationSequence: craneClawGrab,
		  	priority: 3,
		  	monsterOffsetTop: monsterOffsetTop
		  },
		  {
		  	animationName: 'craneClawPickup',
		  	animationSequence: craneClawPickup,
		  	priority: 4
		  },
		  {
		  	animationName: 'monsterPickup',
		  	animationSequence: monsterPickup,
		  	priority: 4,
		  	monsterNumber: monsterNumber,
		  	monsterOffsetTop: monsterOffsetTop
		  },
		  {
		  	animationName: 'craneToDrop',
		  	animationSequence: craneToDrop,
		  	priority: 5,
		  	monsterNumber: monsterNumber,
		  	monsterOffsetTop: monsterOffsetTop
		  },
		  {
		  	animationName: 'monsterToDrop',
		  	animationSequence: monsterToDrop,
		  	priority: 5,
		  	monsterNumber: monsterNumber,
		  	monsterOffsetTop: monsterOffsetTop
		  },
		  {
		  	animationName: 'craneClawDrop',
		  	animationSequence: craneClawDrop,
		  	priority: 6
		  },
		  {
		  	animationName: 'monsterSpin',
		  	animationSequence: monsterSpin,
		  	priority: 7,
		  	monsterNumber: monsterNumber
		  },
		  {
		  	animationName: 'monsterDrop',
		  	animationSequence: monsterDrop,
		  	priority: 7,
		  	monsterNumber: monsterNumber,
		  	monsterRemove: true
		  },
		  {
		  	animationName: 'craneReset',
		  	animationSequence: craneReset,
		  	priority: 8
		  }
		]
		});
	}

	removeComponent(componentArray, componentNumber) {
		this.setState({
			[componentArray] : this.state[componentArray].filter(function(item) {
				return item.props.number !== componentNumber
			})
		});
	}

	createMonster(monsterState, monsterProps) {
		this.setState({
			monsterCount: this.state.monsterCount + 1,
			monsterArray: [...this.state.monsterArray, 
			  <Monster 
			    key={this.state.monsterCount} 
			    id={monsterProps.number}
			    number={monsterProps.number}
			    color = {monsterState.color}
			    species={monsterState.species} 
			    onRemove={this.removeComponent}
			    oncreateFloatingMonster={this.createFloatingMonster}
			  />
			]
		});
	}

	createFloatingMonster(monsterState, monsterProps) {
		console.log(monsterState);
		this.setState({
			floatingMonsterCount: this.state.floatingMonsterCount + 1,
			floatingMonsterArray: [...this.state.floatingMonsterArray, 
			  <FloatingMonster 
			    key={this.state.floatingMonsterCount} 
			    id={'floating-monster-id-' + monsterProps.number}
			    number={monsterProps.number}
			    species={monsterProps.species} 
			    color={monsterState.color}
			  />
			]
		});
	}

	//current animation's done
	currentAnimationFinished(animate) {

		//Unmount this component
		if (animate.monsterRemove) {
			this.removeComponent("gizmoArray", animate.monsterNumber);
		}


		this.setState({	

			animationQueue: this.state.animationQueue.filter(function(item) {
				return item !== animate
			}),
			currentAnimation: this.state.currentAnimation.filter(function(item) {
				return item !== animate
			}),
		});

		//Reset animation queue priority
		if (this.state.animationQueue.length === 0) {
			this.setState({
				animationQueuePriority: 0
			})			
		}
	}

	componentDidUpdate(prevProps, prevState) {
		//We have an updated animation queue
		if ((prevState.animationQueue !== this.state.animationQueue 
			&& this.state.animationQueue.length !== 0
			&& this.state.currentAnimation.length === 0
		)) {

			let animationPriority = this.state.animationQueuePriority;

			animationPriority++;

			let sendToCurrentAnimation = [];
			this.state.animationQueue.forEach(function(animation, index) { 
				if (animation.priority === animationPriority) {				
					sendToCurrentAnimation.push(animation);
				}				
			}, this);

			this.setState({
			  currentAnimation: sendToCurrentAnimation,	
			  animationQueuePriority: animationPriority  
			});
		}		

		//We have current animation(s)
		if (prevState.currentAnimation.length < this.state.currentAnimation.length && this.state.currentAnimation.length !== 0) {			
				this.state.currentAnimation.forEach(function(animate, index) {
					animate.animationSequence(animate).then((resolve) => this.currentAnimationFinished(resolve));			
				}, this);
		}	
	}

	render() {
		return (
		  <div className='gizmos-container'>
		  	<Crane />
		    <section className='gizmo-button-container'>
		      <NewMonsterPartsButton onClick={this.createGizmo} disabled={this.state.gizmoArray.length >= 3 ? true : false} />
		    </section>
			  <section className='gizmo-creater'>
				{this.state.gizmoArray}
			</section>
            <section className='gizmo-converter'>
             <p>need to design a converter</p>
            </section>
            <section className='gizmo-result'>
             <p>need to design a gizmo result</p>
             {this.state.monsterArray}
            </section>
            <section className='floating-monster-container'>
            	{this.state.floatingMonsterArray}
            </section>
	      </div>
		)
	}
}


