	const element = document.getElementById('crane-claw');
   return (cssTransitionToPromise(animate, element, 'background-color', 'green'))
}

const monsterDrop = (animate) => {
	const element = document.getElementById('gizmoId-' + animate.monsterNumber);
   return (cssTransitionToPromise(animate, element, 'top', '100px'))
}

const monsterSpin = (animate) => {
	const element = document.getElementById('gizmoId-' + animate.monsterNumber);
   return (cssTransitionToPromise(animate, element, 'transform', 'rotate(180deg)'))
}

const craneReset = (animate) => {
  const element = document.getElementById('crane');
  return (cssTransitionToPromise(animate, element, 'margin-left', 'calc(-25% - 5px)'))
}

