   return (cssTransitionToPromise(animate.animationName, element, 'margin-left', '70.5%'))
}

const monsterToDrop = (animate) => {
	const element = document.getElementById('gizmoId-' + animate.monsterNumber);
   return (cssTransitionToPromise(animate.animationName, element, 'left', '50%'))
}

const craneClawDrop = (animate) => {
	const element = document.getElementById('crane-claw');
   return (cssTransitionToPromise(animate.animationName, element, 'background-color', 'red'))
}

const monsterDrop = (animate) => {
	const element = document.getElementById('gizmoId-' + animate.monsterNumber);
   return (cssTransitionToPromise(animate.animationName, element, 'transform, top', 'rotate(180deg)'))
}

const monsterDropSpin = (animate) => {
	const element = document.getElementById('gizmoId-' + animate.monsterNumber);
   return (cssTransitionToPromise(animate.animationName, element, 'transform, top', 'rotate(180deg)'))
}

