	setTimeout(function() {
		alert('E');
		return('done')
	}, 2000);
}

const f = () => {
  setTimeout(function() {
		alert('F');
	}, 2000);
}

const g = () => {
  setTimeout(function() {
		alert('G');
	}, 2000);
}

