import React from 'react';
import {Frame} from './frame';

export class Gizmo extends React.Component {
  constructor(props) {
    super(props);    
    this.monsterDOMRef = React.createRef(); // Coordinates in DOM used for animation
    this.state = {
      displayFrame: false,      
      preMountEvents: ''
    };
    this.toggleWidth = this.toggleWidth.bind(this);
    this.toggleCorners = this.toggleCorners.bind(this);
    this.toggleColor = this.toggleColor.bind(this);
    this.componentLifeCycleEvent = this.componentLifeCycleEvent.bind(this);
    this.monsterSelected = this.monsterSelected.bind(this);
  }

  toggleColor() {
  	const possibleBlockColors = ['red', 'orange', 'yellow', 'green', 'blue', 'indigo', 'violet'];
  	const randomColor = Math.floor(Math.random() * possibleBlockColors.length);
    this.setState({
      color: possibleBlockColors[randomColor]
    });
  }

  toggleWidth() {
	this.setState({
		width: this.state.width === '200px' ? '300px' : '200px'
	});
  }

  toggleCorners() {
	this.setState({
		borderRadius: this.state.borderRadius === '0px' ? '50px' : '0px'
	});
  }

  monsterSelected() {
    this.props.onFilterOut(this.props.number, this.monsterDOMRef.current.offsetTop);
  }

  componentLifeCycleEvent(lifeCycleEvent, prevProps, prevState) {
    document.getElementById('gizmo-updates-screen').prepend(lifeCycleEvent);
  }

  componentWillMount() {
	this.setState({
		preMountEvents: 'componentWillMount called'
 	});
  }

  componentDidMount() {
    this.componentLifeCycleEvent('componentDidMount called');
  }

  componentDidUpdate(prevProps, prevState) {
  	if (prevState !== this.state) {
  		this.componentLifeCycleEvent('componentDidUpdate called', prevProps.number, prevState);
  	}
  	if (prevProps.number !== this.props.number) {
  	}
  }

  componentWillUnmount() {
    this.props.onCreateMonster(this.state, this.props);
  }

  render() {

    let frame;

     if (this.state.displayFrame) {
      frame = <Frame currentFrameType={this.state.currentFrameType} cycleFrameType={this.cycleFrameType} currentFrameOption={this.state.currentFrameOption} resetFrameOption={this.resetFrameOption} />;
    }

	return (    
	  <div className='gizmo' id={'gizmoId'} ref={this.monsterDOMRef} >
      {frame}
    </div>
    )
  }
}
