import React from 'react';
import './wheel.css';

export class Wheel extends React.Component {
	constructor(props) {
		super(props);	
	}

	state = {
		currentAxle: 0
	}

	componentDidUpdate(prevProps, prevState) {
		if (prevState.currentAxle === 0) {
 			this.setState({
				currentAxle: this.state.currentAxle + 1
			})
		}		
	}

	render() {
		return(<div id={'wheelOption' + this.props.currentWheelOption + this.state.currentAxle} className='wheel' />);
	}
}
