import React from 'react';
import {ChildGizmo} from './childGizmo';

export class ParentGizmo extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			backgroundColor: '#fff',
			width: '200px',
			height: '100px',
			borderRadius: '0px'
		};
		this.toggleColor = this.toggleColor.bind(this);
		this.toggleHeight = this.toggleHeight.bind(this);
		this.toggleWidth = this.toggleWidth.bind(this);
		this.toggleCorners = this.toggleCorners.bind(this);
	}

	toggleColor() {	
		this.setState({
			backgroundColor: this.state.backgroundColor === '#fff' ? '#000' : '#fff'			
		});
	}

	toggleHeight() {
		this.setState({
			height: this.state.height === '100px' ? '200px' : '100px'
		});
	}

	toggleWidth() {
		this.setState({
			width: this.state.width === '200px' ? '300px' : '200px'
		});
	}

	toggleCorners() {
		this.setState({
			borderRadius: this.state.borderRadius === '0px' ? '50px' : '0px'
		});
	}

	logLifeCycleDetail(value) {
		const itemLi = value.map((value, i) =>
			<li key={'item_' + i}>{value}</li>
		);
		return itemLi
	}

	componentWillMount() {
		console.log('componentWillMount: immediately before Stripe is rendered');
		this.logLifeCycleDetail(['componentWillMount']);
	}

	render() {
		return (
			<div>
				<div style={{
					backgroundColor: this.state.backgroundColor,
					width: this.state.width,
					height: this.state.height,
					borderRadius: this.state.borderRadius
					}}>
				</div>
				<div className='gizmo-details'>
					<ul>

				    </ul>
				</div>
				<ChildGizmo 
					onChangeColor={this.toggleColor}
					onChangeHeight={this.toggleHeight}
					onChangeWidth={this.toggleWidth}
					onChangeCorners={this.toggleCorners}
				/>
			</div>
			)
	}
}
