import React from 'react';
import {NewMonsterPartsButton} from './newMonsterPartsButton';
import {Gizmo} from './gizmo';
import {Crane} from './crane';
import {Frame} from './frame';
import {Monster} from './monster';
import {FloatingMonster} from './floatingMonster';
import * as animation from './gizmoAnimations.js';
import './gizmo.css';
import './mechanic.css';
import './monster.css';
import './floatingMonster.css';

export class GizmoSection extends React.Component {
  constructor(props) {
	super(props);
	this.convertMonster = this.convertMonster.bind(this);
	this.toggleComponent = this.toggleComponent.bind(this);
	this.createMonster = this.createMonster.bind(this);
	this.bringInElement = this.bringInElement.bind(this);
	this.swapFrame = this.swapFrame.bind(this);
	this.createFloatingMonster = this.createFloatingMonster.bind(this);
  }
  state = {
    gizmoCount: 0,
    displayGizmo: false,
    animationQueue: [],
    animationQueuePriority: 0,
    currentAnimation: [],
    monsterCount: 0,
    monsterArray: [],
    floatingMonsterCount: 0,
    floatingMonsterArray: [],
    craneCurrentlyAnimating: false,
    displayFrame: false,
    currentFrameType: 0
  }

	bringInElement(element) {	
		if (!this.state.craneCurrentlyAnimating) {
		this.setState({	
		craneCurrentlyAnimating: true,
		displayGizmo: true,
		animationQueue: [
		  {
		  	animationName: 'craneOffScreen',
		  	animationSequence: animation.craneOffScreen,
		  	priority: 1
		  },
		  {
		  	animationName: 'craneIntoScreen',
		  	animationSequence: animation.craneIntoScreen,
		  	priority: 2
		  },
		  {
		  	animationName: 'elementIntoScreen',
		  	animationSequence: animation.elementIntoScreen,
		  	element: element,
		  	priority: 2
		  },
		  {
		  	animationName: 'craneClawLeftOpen',
		  	animationSequence: animation.craneClawLeftOpen,
		  	priority: 3  	
		  },
		  {
		  	animationName: 'craneClawRightOpen',
		  	animationSequence: animation.craneClawRightOpen,
		  	priority: 3  	
		  },		  
		  {
		  	animationName: 'elementFall',
		  	animationSequence: animation.elementFall,
		  	element: element,
		  	monsterOffsetTop: 0,
		  	priority: 4
		  },
		  {
		  	animationName: 'craneClawLeftClose',
		  	animationSequence: animation.craneClawLeftClose,
		  	priority: 5  	
		  },
		  {
		  	animationName: 'craneClawRightClose',
		  	animationSequence: animation.craneClawRightClose,
		  	priority: 5  	
		  },
		  {
		  	animationName: 'craneReset',
		  	animationSequence: animation.craneReset,
		  	priority: 5
		  }
		  ]
		});		
	 }
	}

	swapFrame() {		

	//First new frame
	  if(!this.state.displayFrame) {
	  	console.log('firstFrame');
	     this.toggleComponent('displayFrame');
	     this.bringInElement('frame');
	  }
	  else {
	  	console.log('anotherFrame');
	    //Run the mechanic animation
	    this.mechanicRemovePart();

	    //Create the next frame component
	    //this.toggleDisplayFrame();

	    //Run the frame animation
	    //this.bringInElement('frame');
	  }
	}

	// toggleDisplayFrame() {
	//   this.setState({
 //        displayFrame: !this.state.displayFrame
 //      })
 //      if (this.state.displayFrame) {
 //      	this.setState({
 //      		currentFrameType: this.state.currentFrameType + 1
 //      	})
 //      }
	// }

	// pickNewFrame() {
	// 	if (!this.state.craneCurrentlyAnimating) {
	// 	this.setState({	
	// 	craneCurrentlyAnimating: true,
	// 	animationQueue: [
	// 	   {
	// 	  	animationName: 'craneOffScreen',
	// 	  	animationSequence: animation.craneOffScreen,
	// 	  	priority: 1
	// 	  }
	// 	]
	// 	});

	//   }
	// }

	mechanicRemovePart() {
		if (!this.state.craneCurrentlyAnimating) {
		this.setState({	
			animationQueue: [
			  {
			  	animationName: 'mechanicAlign',
			  	animationSequence: animation.mechanicAlign,
			  	priority: 1
			  },
			  {
			  	animationName: 'mechanicRaise',
			  	animationSequence: animation.mechanicRaise,
			  	priority: 2
			  },
			  {
			  	animationName: 'mechanicShake',
			  	animationSequence: animation.mechanicShake,
			  	priority: 3
			  },		
			  {
			  	animationName: 'mechanicLower',
			  	animationSequence: animation.mechanicLower,
			  	priority: 4
			  },
			   {
			  	animationName: 'frameLower',
			  	animationSequence: animation.frameLower,
			  	priority: 4
			  },
			  {
			  	animationName: 'mechanicOffScreen',
			  	animationSequence: animation.mechanicOffScreen,
			  	priority: 5
			  },
			  {
			  	animationName: 'frameOffScreen',
			  	animationSequence: animation.frameOffScreen,
			  	priority: 5,
			  	toggleComponent: 'displayFrame'
			  },
			  {
			  	animationName: 'mechanicReset',
			  	animationSequence: animation.mechanicReset,
			  	priority: 6			  	
			  },
			  {
			  	animationName: 'craneOffScreen',
			  	animationSequence: animation.craneOffScreen,
			  	priority: 6			  	
			  },
			  {
			  	animationName: 'craneIntoScreen',
			  	animationSequence: animation.craneIntoScreen,
			  	priority: 7
			  },
			  {
			  	animationName: 'elementIntoScreen',
			  	animationSequence: animation.elementIntoScreen,
			  	element: 'frame',
			  	priority: 7
			  },
			  {
			  	animationName: 'craneClawLeftOpen',
			  	animationSequence: animation.craneClawLeftOpen,
			  	priority: 8  	
			  },
			  {
			  	animationName: 'craneClawRightOpen',
			  	animationSequence: animation.craneClawRightOpen,
			  	priority: 8  	
			  },		  
			  {
			  	animationName: 'elementFall',
			  	animationSequence: animation.elementFall,
			  	element: 'frame',
			  	monsterOffsetTop: 0,
			  	priority: 9
			  },
			  {
			  	animationName: 'craneClawLeftClose',
			  	animationSequence: animation.craneClawLeftClose,
			  	priority: 10 	
			  },
			  {
			  	animationName: 'craneClawRightClose',
			  	animationSequence: animation.craneClawRightClose,
			  	priority: 10 	
			  },
			  {
			  	animationName: 'craneReset',
			  	animationSequence: animation.craneReset,
			  	priority: 10
			  }
			]			
		});
		}
	}

	convertMonster(monsterNumber) {
		if (!this.state.craneCurrentlyAnimating) {
		this.setState({	
		craneCurrentlyAnimating: true,
		animationQueue: [
		  {
		  	animationName: 'craneIntoScreen',
		  	animationSequence: animation.craneIntoScreen,
		  	priority: 1
		  },
		  {
		  	animationName: 'craneClawLeftOpen',
		  	animationSequence: animation.craneClawLeftOpen,
		  	priority: 2	
		  },
		  {
		  	animationName: 'craneClawRightOpen',
		  	animationSequence: animation.craneClawRightOpen,
		  	priority: 2  	
		  },
		  {
		  	animationName: 'craneClawLower',
		  	animationSequence: animation.craneClawLower,
		  	priority: 2
		  },
		  {
		  	animationName: 'craneClawLeftClose',
		  	animationSequence: animation.craneClawLeftClose,
		  	priority: 3
		  },
		  {
		  	animationName: 'craneClawRightClose',
		  	animationSequence: animation.craneClawRightClose,
		  	priority: 3
		  },
		  {
		  	animationName: 'craneClawPickup',
		  	animationSequence: animation.craneClawPickup,
		  	priority: 4
		  },
		  {
		  	animationName: 'gizmoPickup',
		  	animationSequence: animation.gizmoPickup,
		  	priority: 4,
		  	monsterNumber: this.state.gizmoCount
		  },
		  {
		  	animationName: 'craneToDrop',
		  	animationSequence: animation.craneToDrop,
		  	priority: 5,
		  	monsterNumber: this.state.gizmoCount
		  },
		  {
		  	animationName: 'monsterToDrop',
		  	animationSequence: animation.monsterToDrop,
		  	priority: 5,
		  	monsterNumber: this.state.gizmoCount
		  },
		  {
		  	animationName: 'craneClawLeftOpen',
		  	animationSequence: animation.craneClawLeftOpen,
		  	priority: 6
		  },
		  {
		  	animationName: 'craneClawRightOpen',
		  	animationSequence: animation.craneClawRightOpen,
		  	priority: 6
		  },
		  {
		  	animationName: 'monsterDrop',
		  	animationSequence: animation.monsterDrop,
		  	priority: 7,
		  	monsterNumber: this.state.gizmoCount,
		  	toggleComponent: 'displayGizmo'
		  },
		  {
		  	animationName: 'craneClawLeftClose',
		  	animationSequence: animation.craneClawLeftClose,
		  	priority: 8
		  },
		  {
		  	animationName: 'craneClawRightClose',
		  	animationSequence: animation.craneClawRightClose,
		  	priority: 8
		  },
		  {
		  	animationName: 'craneReset',
		  	animationSequence: animation.craneReset,
		  	priority: 9
		  }
		]
		});
	 }
	}

	createMonster(monsterState, monsterProps) {
		this.setState({
			monsterCount: this.state.monsterCount + 1,
			monsterArray: [...this.state.monsterArray, 
			  <Monster 
			    key={this.state.monsterCount} 
			    id={monsterProps.number}
			    number={monsterProps.number}
			    color = {monsterState.color}
			    species={monsterState.species} 
			    onRemove={this.removeComponent}
			    oncreateFloatingMonster={this.createFloatingMonster}
			  />
			]
		});
	}

	createFloatingMonster(monsterState, monsterProps) {
		this.setState({
			floatingMonsterCount: this.state.floatingMonsterCount + 1,
			floatingMonsterArray: [...this.state.floatingMonsterArray, 
			  <FloatingMonster 
			    key={this.state.floatingMonsterCount} 
			    id={'floating-monster-id-' + monsterProps.number}
			    number={monsterProps.number}
			    species={monsterProps.species} 
			    color={monsterState.color}
			  />
			]
		});
	}

	//current animation's done
	currentAnimationFinished(animate) {

		this.setState({	
			animationQueue: this.state.animationQueue.filter(function(item) {
				return item !== animate
			}),
			currentAnimation: this.state.currentAnimation.filter(function(item) {
				return item !== animate
			}),
		});

		//Remove component
		if (animate.toggleComponent) {
			this.toggleComponent(animate.toggleComponent);
		}

		//Reset animation queue priority
		if (this.state.animationQueue.length === 0) {
			this.setState({
				animationQueuePriority: 0,
				craneCurrentlyAnimating: false
			});	  
		}
	}

	toggleComponent(componentState) {		
		this.setState({
			[componentState]: !this.state[componentState]
		});
		console.log('toggling:' + componentState + ' from this ' + this.state[componentState]);
	}

	componentDidUpdate(prevProps, prevState) {
		//We have an updated animation queue
		if ((prevState.animationQueue !== this.state.animationQueue 
			&& this.state.animationQueue.length !== 0
			&& this.state.currentAnimation.length === 0
		)) {

			let animationPriority = this.state.animationQueuePriority;

			animationPriority++;

			let sendToCurrentAnimation = [];
			this.state.animationQueue.forEach(function(animation, index) { 
				if (animation.priority === animationPriority) {				
					sendToCurrentAnimation.push(animation);
				}				
			}, this);

			this.setState({
			  currentAnimation: sendToCurrentAnimation,	
			  animationQueuePriority: animationPriority  
			});
		}		

		//We have current animation(s)
		if (prevState.currentAnimation.length < this.state.currentAnimation.length && this.state.currentAnimation.length !== 0) {	
			this.state.currentAnimation.forEach(function(animate, index) {					
				animate.animationSequence(animate).then((resolve) => this.currentAnimationFinished(resolve));			
			}, this);
		}	
	}

	// cycleFrameType() {

	// }

	// cycleFrameOption() {

	// }

	render() {

		let gizmo;
		let frame;

		if(this.state.displayGizmo) {
			gizmo = <Gizmo number={this.state.gizmoCount} />
		}

		if (this.state.displayFrame) {

			frame = <Frame swapFrame={this.swapFrame} currentFrameType={this.state.currentFrameType} />
		}

		return (
		  <div className='gizmos-container'>
		  	<Crane />
			  <section className='gizmo-creater-container'>	
			  	{frame}		   
				{gizmo}			
			</section>
			<div className='gizmo-forklift' />
			<section className='gizmo-mechanic-container'>
			<div id='gizmo-mechanic-outer'>
			  <div id='gizmo-mechanic-upper' />
			    <div className='gizmo-mechanic-tower' />
			    <div className='gizmo-mechanic-base'>
			    <div className='gizmo-mechanic-base-gear-container'>
 				  <div className='gizmo-mechanic-base-gear-left'>
				    <div className="center"></div>
				    <div className="tooth"></div>
				    <div className="tooth"></div>
				    <div className="tooth"></div>
				    <div className="tooth"></div>
				  </div>
				<div className='gizmo-mechanic-base-gear-right'>
				    <div className="center"></div>
				    <div className="tooth"></div>
				    <div className="tooth"></div>
				    <div className="tooth"></div>
				    <div className="tooth"></div>
				</div>
				</div>
			    </div>			    
			  </div>
			  <div className='gizmo-mechanic-track-container'>
			      <div className='gizmo-mechanic-track-upper'/>
			    </div>
			</section>
            <section className='gizmo-converter-container'>
              <div className='gizmo-hopper-upper' />
              <div className='gizmo-hopper-lower' />
              <div className='gizmo-converter'>
                <div className='gizmo-button-new-gizmo-container'>
                  <div className='gizmo-button gizmo-button-new' onMouseDown={gizmoButtonMouseDown} onMouseUp={gizmoButtonMouseUp} onMouseOut={gizmoButtonMouseUp} onClick={() => this.bringInElement('gizmoId-' + this.state.gizmoCount)}>
                    <i className="fas fa-plus"></i>
                  </div>
                  <div className='gizmo-button-detail' />
                </div>                
                <div className='gizmo-button-paint-container'>
                	<div className='gizmo-button gizmo-button-paint' onMouseDown={gizmoButtonMouseDown} onMouseUp={gizmoButtonMouseUp} onMouseOut={gizmoButtonMouseUp}>
                		<i className="fas fa-paint-brush"></i>
                	</div>
                	<div className='gizmo-button-detail' />
                </div>
                 <div className='gizmo-button-wheels-container'>
                	<div className='gizmo-button gizmo-button-wheels' onMouseDown={gizmoButtonMouseDown} onMouseUp={gizmoButtonMouseUp} onMouseOut={gizmoButtonMouseUp}>
                	  <i className="far fa-futbol"></i>
                	</div>
                	<div className='gizmo-button-detail' />
                </div>
                 <div className='gizmo-button-frame-container'>
                	<div className='gizmo-button gizmo-button-frame' onMouseDown={gizmoButtonMouseDown} onMouseUp={gizmoButtonMouseUp} onMouseOut={gizmoButtonMouseUp} onClick={this.swapFrame}>
                	  <i className="fas fa-cogs"></i>
                	</div>
                	<div className='gizmo-button-detail' />
                </div>
                 <div className='gizmo-button-windows-container'>
                	<div className='gizmo-button gizmo-button-windows' onMouseDown={gizmoButtonMouseDown} onMouseUp={gizmoButtonMouseUp} onMouseOut={gizmoButtonMouseUp}>
                	  <i className="fas fa-border-all"></i>
                	</div>
                	<div className='gizmo-button-detail' />
                </div>
                <div className='gizmo-button-decals-container'>
                	<div className='gizmo-button gizmo-button-decals' onMouseDown={gizmoButtonMouseDown} onMouseUp={gizmoButtonMouseUp}  onMouseOut={gizmoButtonMouseUp}>
                	  <i className="fab fa-autoprefixer"></i>
                	</div>
                	<div className='gizmo-button-detail' />
                </div>
                 <div className='gizmo-button-build-container'>
                	<div className='gizmo-button-large gizmo-button-build' onMouseDown={gizmoButtonMouseDown} onMouseUp={gizmoButtonMouseUp}  onMouseOut={gizmoButtonMouseUp} onClick={this.convertMonster}>
                	  <i className="fas fa-bold"></i>
                	</div>
                	<div className='gizmo-button-large-detail' />
                </div>
              </div>
            </section>
           <div className='gizmo-updates-container'>
			  		<ul id='gizmo-updates-screen' />			  		
				</div>
            <section className='gizmo-result-container'>
             {this.state.monsterArray}
             <div className='gizmo-result-track-container'>
               <div className='gizmo-result-track-upper' />
             </div>
            </section>
            <section className='floating-monster-container'>
            	{this.state.floatingMonsterArray}
            </section>
	      </div>
		)
	}
}

const gizmoButtonMouseDown = (e) => {
	e.target.classList.add('gizmo-button-mousedown');
}

const gizmoButtonMouseUp = (e) => {
	e.target.classList.remove('gizmo-button-mousedown');
}
