import React from 'react';
import {Gizmo} from './gizmo';

import './gizmo.css';

export class GizmoSection extends React.Component {
  constructor(props) {
	super(props);
	this.gizmoRef = React.createRef();
	this.toggleGizmoButton = this.toggleGizmoButton.bind(this);
  }
		state = {
		  gizmoCount: 0,
		  gizmoChildren: [],
		  gizmoButtonFunction: ''
		}

	createGizmo = () => {
		this.setState({
			gizmoCount: this.state.gizmoCount + 1,
			gizmoButtonFunction: 'createGizmo'
		});
		this.state.gizmoChildren.push(<Gizmo key={this.state.gizmoCount} number={this.state.gizmoCount} ref={this.gizmoRef} />);
	}

	convertGizmo() {
	  this.setState({
	  	gizmoButtonFunction: 'convertGizmo'
	  });
	  console.log(this.gizmoRef.current);
       this.setState({
       	gizmoChildren: this.state.gizmoChildren.filter(function(gizmoChild) { 
         return gizmoChild !== this.gizmoChildren[0]
       })});
	}

	toggleGizmoButton() {
	  if (this.state.gizmoButtonFunction === 'createGizmo') {
	  	this.convertGizmo();	  	
	  }
	  else {
	  	this.createGizmo();
	  }
	}

	render() {
		return (
		  <div className='gizmos-container'>
		    <section className="gizmo-button-container">
		      <GizmoButton onClick={this.toggleGizmoButton} />
		    </section>
			<ParentGizmoContainer>
				{this.state.gizmoChildren}
			</ParentGizmoContainer>
            <section className="gizmo-converter">
             <p>need to design a converver</p>
            </section>
            <section className="gizmo-result">
             <p>need to design a gizmo result</p>
            </section>
	      </div>
		)
	}
}

const ParentGizmoContainer = props => (
  <div className="gizmo-creater">
	<div className="GizmosGoHere">
	  {props.children}
    </div>
  </div>
);

class GizmoButton extends React.Component {
  render() {

    return (
      <button onClick={this.props.onClick}>Make a Gizmo</button>
    ) 

  }
