import React from 'react';
import {Gizmo} from './gizmo';

export class GizmoSection extends React.Component {

		state = {
			gizmoCount : 0
		}


	createGizmo = () => {
		this.setState({
			gizmoCount: this.state.gizmoCount + 1 
		});
		console.log('createGizmo called');

	}

	render() {
		const gizmoChildren = [];
		for(let i = 0; i < this.state.gizmoCount; i++) {
			console.log(i);
			console.log(this.state.gizmoCount);
			gizmoChildren.push(<Gizmo key={i} number={i} />)
			console.log(gizmoChildren);

		}

		return (<div>

			<ParentGizmoContainer addGizmo={this.createGizmo}>
				{gizmoChildren}
			</ParentGizmoContainer>
			</div>)
	}
}


const ParentGizmoContainer = props => (
	<div className="testing">
	<button onClick={props.addGizmo}>Make me a Gizmo</button>
		<div className="GizmosGoHere">
			{props.gizmoChildren}
			</div>
	</div>
);
