//Set of animations that pick up a gizmo, move it to the converter, drop it, and reset.

export const craneToPickup = (animate) => {
  const element = document.getElementById('crane');
  return (cssTransitionToPromise(animate, element, 'margin-left', '50%'))
}

export const craneClawLower = (animate) => {
  const element = document.getElementById('crane-claw');
   return (cssTransitionToPromise(animate, element, 'top', animate.monsterOffsetTop + 'px'))
}

export const craneClawGrab = (animate) => {
  const element = document.getElementById('crane-claw');
   return (cssTransitionToPromise(animate, element, 'background-color', 'red'))
}

export const craneClawPickup = (animate) => {
	const element = document.getElementById('crane-claw');
   return (cssTransitionToPromise(animate, element, 'top', '0'))
}

export const monsterPickup = (animate) => {
	const element = document.getElementById('gizmoId-' + animate.monsterNumber);
   return (cssTransitionToPromise(animate, element, 'top', - animate.monsterOffsetTop + 'px'))
}

export const craneToDrop = (animate) => {
	const element = document.getElementById('crane');
   return (cssTransitionToPromise(animate, element, 'margin-left', '80%'))
}

export const monsterToDrop = (animate) => {
	const element = document.getElementById('gizmoId-' + animate.monsterNumber);
   return (cssTransitionToPromise(animate, element, 'left', '50%'))
}

export const craneClawDrop = (animate) => {
	const element = document.getElementById('crane-claw');
   return (cssTransitionToPromise(animate, element, 'background-color', 'green'))
}

export const monsterDrop = (animate) => {
	const element = document.getElementById('gizmoId-' + animate.monsterNumber);
   return (cssTransitionToPromise(animate, element, 'top', '100px'))
}

export const monsterSpin = (animate) => {
	const element = document.getElementById('gizmoId-' + animate.monsterNumber);
   return (cssTransitionToPromise(animate, element, 'transform', 'rotate(180deg)'))
}

export const craneReset = (animate) => {
  const element = document.getElementById('crane');
  return (cssTransitionToPromise(animate, element, 'margin-left', 0))
}
