	convertMonster() {
		if (!this.state.craneCurrentlyAnimating) {
		this.setState({	
		craneCurrentlyAnimating: true,
		animationQueue: [
		  {
		    animationName: 'lowerLift',
			animationSequence: animation.lowerLift,
			priority: 1		  	
	      },
	      {
		    animationName: 'lowerGizmo',
			animationSequence: animation.lowerGizmo,
			priority: 1		  	
	      },
