import React from 'react';
import {ChildGizmo} from './childGizmo';

const ListItem = ({ value }) => (
  <li>{value}</li>
);

const List = ({ items }) => (
  <ul>
    {
      items.map((item, i) => <ListItem key={i} value={item} />)
    }
  </ul>
);

export class ParentGizmo extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      backgroundColor: '#fff',
      width: '200px',
      height: '100px',
      borderRadius: '0px',
      componentEvents: ['Component Will Mount', 'Component Did Mount'],
      latestComponentEvent: ''
    };
    this.toggleColor = this.toggleColor.bind(this);
    this.toggleHeight = this.toggleHeight.bind(this);
    this.toggleWidth = this.toggleWidth.bind(this);
    this.toggleCorners = this.toggleCorners.bind(this);
    this.onComponentStateChange = this.onComponentStateChange.bind(this);
}

toggleColor() {	
	this.setState({
		backgroundColor: this.state.backgroundColor === '#fff' ? '#000' : '#fff'			
	});
	onComponentStateChange()
}

toggleHeight() {
	this.setState({
		height: this.state.height === '100px' ? '200px' : '100px'
	});
}

toggleWidth() {
	this.setState({
		width: this.state.width === '200px' ? '300px' : '200px'
	});
}

toggleCorners() {
	this.setState({
		borderRadius: this.state.borderRadius === '0px' ? '50px' : '0px'
	});
}

onComponentStateChange() {
  console.log('onComponentStateChange called');
  const { latestComponentEvent, componentEvents } = this.state;   	
  const nextState = [...componentEvents, latestComponentEvent];
  console.log(nextState);
  this.setState({ componentEvents: nextState });
}

componentWillMount() {
	console.log('componentWillMount called');

}

componentDidMount() {
	console.log('componentDidMount called');
}

componentDidUpdate(prevProps, prevState) {
	console.log('componentWillUpdate called')
}

render() {
	return (
		<div>
			<div style={{
				backgroundColor: this.state.backgroundColor,
				width: this.state.width,
				height: this.state.height,
				borderRadius: this.state.borderRadius
				}}>
			</div>
			<div className='gizmo-details'>
			      <List items={this.state.componentEvents} />
			</div>
			<ChildGizmo 
				onChangeColor={this.toggleColor}
				onChangeHeight={this.toggleHeight}
				onChangeWidth={this.toggleWidth}
				onChangeCorners={this.toggleCorners}
			/>
		</div>
		)
	}
}
