import React from 'react';
import {ChildGizmo} from './childGizmo';

export class ParentGizmo extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			color: '#fff',
			width: '200px',
			height: '100px',
			borderRadius: '0px'
		}
		this.toggleColor = this.toggleColor.bind(this);
	}

	toggleColor() 
	{
		this.setState(this.state.color === '#fff' ? this.state.color = '#000' : '#fff');
	}

	render() {
		return <ChildGizmo onClick={this.toggleColor} />
	}
}
