import React from 'react';

export class GizmoControls extends React.Component {

	render() {
		return(
			<div className='gizmo-converter'>
			   <div className='gizmo-button-new-gizmo-container'>
                  <div className='gizmo-button gizmo-button-new' 
                  	onMouseDown={gizmoButtonMouseDown} 
                  	onMouseUp={gizmoButtonMouseUp} 
                  	onMouseOut={gizmoButtonMouseUp} 
                  	onClick={() => this.bringInElement('gizmoId')}
                  >
                    <i className="fas fa-plus"></i>
                  </div>
                  <div className='gizmo-button-detail' />
                </div>                

			<div className='gizmo-button-paint-container'>
                	<div className='gizmo-button gizmo-button-diabled gizmo-button-paint '                	  
                	  onMouseDown={gizmoButtonMouseDown} 
                	  onMouseUp={gizmoButtonMouseUp} 
                	  onMouseOut={gizmoButtonMouseUp} 
                	  onClick={() => this.mechanicWork(this.cycleFrameOption)}
                	>
                		<i className="fas fa-paint-brush"></i>
                	</div>
                	<div className='gizmo-button-detail' />
                </div>  
			<div className='gizmo-button-frame-container'>
                	<div className='gizmo-button gizmo-button-frame' 
                	  onMouseDown={gizmoButtonMouseDown} 
                	  onMouseUp={gizmoButtonMouseUp} 
                	  onMouseOut={gizmoButtonMouseUp} 
                	  onClick={this.newFrame}>
                	  <i className="fas fa-cogs"></i>
                	</div>
                	<div className='gizmo-button-detail' />
                </div>
			<div className='gizmo-button-decals-container'>
                	<div className='gizmo-button gizmo-button-decals' onMouseDown={gizmoButtonMouseDown} onMouseUp={gizmoButtonMouseUp}  onMouseOut={gizmoButtonMouseUp}>
                	  <i className="fab fa-autoprefixer"></i>
                	</div>
                	<div className='gizmo-button-detail' />
                </div>
                 <div className='gizmo-button-build-container'>
                	<div className='gizmo-button-large gizmo-button-build' onMouseDown={gizmoButtonMouseDown} onMouseUp={gizmoButtonMouseUp}  onMouseOut={gizmoButtonMouseUp} onClick={this.convertMonster}>
                	  <i className="fas fa-bold"></i>
                	</div>
                	<div className='gizmo-button-large-detail' />
                </div>
                </div>
		)	
	}
}
