import React from 'react';


export class FloatingMonster extends React.Component {
	constructor(props) {
		super(props);
		this.boo = this.boo.bind(this);
		this.floatingMonsterRef = React.createRef();
	}


boo(floatingMonster) {
	console.log('boo' + floatingMonster);
  let viewport = ['top', 'right', 'bottom', 'left'];
  let side = viewport[Math.floor(Math.random() * viewport.length)];
  console.log(side);

  //Pick a location on that side
  let sideLocation = Math.random() * 100 + '%';
  console.log(sideLocation);

  //Pick a Monster
  let selectedMonster = this.state.floatingMonsterArray[Math.floor(Math.random() * this.state.floatingMonsterArray.length)];
  console.log(JSON.stringify(selectedMonster));

  //Animate it
  console.log(floatingMonster);
  switch(side) {
	case 'top': 
	window.setTimeout( function() {
      document.getElementById(floatingMonster).style['left'] = sideLocation;
	  document.getElementById(floatingMonster).className += ' floating-monster-top';
	  }, 50);
	break;

	case 'right':
	window.setTimeout( function() {
      document.getElementById(floatingMonster).style['top'] = sideLocation;
	  document.getElementById(floatingMonster).className += ' floating-monster-right';  
	  }, 50);
	break;

	case 'bottom':
	window.setTimeout( function() {
	  document.getElementById(floatingMonster).style['left'] = sideLocation;
      document.getElementById(floatingMonster).className += ' floating-monster-bottom';
      }, 50);
	break;

	case 'left':
	window.setTimeout( function() {
	  document.getElementById(floatingMonster).style['top'] = sideLocation;
      document.getElementById(floatingMonster).className += ' floating-monster-left';
      }, 50);
	break;
	default:
	return
  }
}

componentDidMount() {
	(function loop() {
	    var rand = Math.round(Math.random() * (3000 - 500)) + 500;
	    setTimeout(function() {
	            this.boo(this.floatingMonsterRef)//Need to send this floating monster
	            loop();  
	    }.bind(this), rand);
	}());

	//this.loop();
}

render() {
		return (
			<div id={this.props.id} className={`floating-monster ${this.props.species}`} ref={this.floatingMonsterRef}>
				<div className='head' />
				<div className='torso' />
				<div className='left-arm' />
				<div className='right-arm' />
			</div>
				)
}

}
