import React from 'react';
import {Gizmo} from './gizmo';
import './gizmo.css';

export class GizmoSection extends React.Component {
  constructor(props) {
	super(props);
	this.gizmoRef = React.createRef();
	//this.convertGizmo = this.convertGizmo.bind(this);
  }
		state = {
		  gizmoCount : 0,
		  gizmoArray : [] 
		  //gizmoButtonFunction: ''
		}

	createGizmo = () => {
		this.setState({
			gizmoCount : this.state.gizmoCount + 1,
			gizmoArray: [...this.state.gizmoArray, <Gizmo key={this.state.gizmoCount} number={this.state.gizmoCount} ref={this.gizmoRef} onFilterOut={this.crane} />]			
		});
		console.log(this.gizmoRef.current);
	}

	convertGizmo(toBeFilteredOut) {
		console.log('Parent' + toBeFilteredOut);

		// run some animation here, wait till its done, and then fire set state like below.

		this.setState({
			gizmoArray : this.state.gizmoArray.filter(function(item) {
				return item.props.number !== toBeFilteredOut
			})
		});
	}

	crane(monster) {
		document.getElementById('crane').classList.add('crane-move-right');
		this.convertGizmo();
	}

	render() {
		return (
		  <div className='gizmos-container'>
		  	<div className="crane-container">
		  	  <div id="crane">
		  	    <div className="crane-top" />
		  	    <div className="crane-line" />
		  	    <div className="crane-claw" />
		  	  </div>
		  	</div>
		    <section className="gizmo-button-container">
		      <GizmoButton onClick={this.createGizmo} />
		    </section>
			  <section className="gizmo-creater">
				{this.state.gizmoArray}
			</section>
            <section className="gizmo-converter">
             <p>need to design a converter</p>
            </section>
            <section className="gizmo-result">
             <p>need to design a gizmo result</p>
            </section>
	      </div>
		)
	}
}

class GizmoButton extends React.Component {
  render() {

    return (
      <button onClick={this.props.onClick}>Make a Gizmo</button>
    ) 

  }
}
