import React from 'react';
import {Gizmo} from './gizmo';
import {Frame} from './frame';

export class Vehicle extends React.Component {
	constructor(props) {
	  super(props);

	  this.state = {
	    displayFrame: false
	  }
	}

	componentWillUpdate(prevProps, prevState) {		
		if (this.props.displayFrame && this.state.displayFrame === false) {						
			this.setState({
				displayFrame: true
			})
		}
		if (!this.props.displayFrame && this.state.displayFrame === true) {						
			this.setState({
				displayFrame: false
			})
		}
	}

	// cycleFrameType = (frameTypeCount) => {
	// 	let nextFrame= this.state.currentFrameType;
	// 	if (this.state.currentFrameType <= frameTypeCount) {
	// 		nextFrame++;
	// 	}
	// 	else {
	// 		nextFrame = 0;
	// 	}
	// 	this.setState({
	// 		currentFrameType: nextFrame		
	// 	});
	// }

	cycleFrameOption = () => {
		let nextOption= this.state.currentFrameOption;
		if (this.state.currentFrameOption <= 6) {
			nextOption++;
		}
		else {
			nextOption = 0;
		}
		this.setState({
			currentFrameOption: nextOption		
		});
	}

	resetFrameOption = () => {
		this.setState({
			currentFrameOption: 0
		});
	}

	render() {

		let frame

		if (this.state.displayFrame) {
			frame = <Frame currentFrameType={this.props.currentFrameType} cycleFrameType={this.cycleFrameType} currentFrameOption={this.props.currentFrameOption} resetFrameOption={this.resetFrameOption} />
		}

		return (
			<div className='vehicle' id={'vehicleId' + this.props.currentVehicleId}>
				<Gizmo onCreateMonster={this.createMonster} currentFrameType={this.props.currentFrameType} />				
				{frame}
			</div>
		)
	}
}
