import React from 'react';
import {ChildGizmo} from './childGizmo';

export class ParentGizmo extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			backgroundColor: '#fff',
			width: '200px',
			height: '100px',
			borderRadius: '0px'
		}
		this.toggleColor = this.toggleColor.bind(this);
	}

	toggleColor() {	
		this.setState({
			scale: this.state.scale > 1 ? 1 : 1.5
		});
	}

	toggleHeight() {
		this.setState({})
	}

	render() {
		return (
			<div>
			<div style={{backgroundColor: this.state.backgroundColor, width: this.state.width, height: this.state.height}}></div>
			<ChildGizmo onClick={this.toggleColor} />
			</div>
			)
	}
}
