import React from 'react';
import {ParentGizmo} from './parentGizmo';

export class Gizmos extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			gizmoCount : 0
		}
		this.createGizmo = this.createGizmo.bind(this);
	}

	createGizmo() {
		this.setState({
			gizmoCount: this.state.gizmoCount++
		})
		console.log(this.state.gizmoCount);
	}

	render() {
		return (

			<div>
				<button onClick={this.createGizmo}>Make me a Gizmo</button>
			<ParentGizmo />
			</div>
			)
	}
}
