import React from 'react';
import {Gizmo} from './gizmo';
import {Crane} from './crane';
import './gizmo.css';

export class GizmoSection extends React.Component {
  constructor(props) {
	super(props);
	this.convertMonster = this.convertMonster.bind(this);
	this.pickUpMonster = this.pickUpMonster.bind(this);
	this.removeMonster = this.removeMonster.bind(this);
	this.currentAnimationFinished = this.currentAnimationFinished.bind(this);
  }
  state = {
    gizmoCount : 0,
    gizmoArray : [],
    animationQueue: [],
    animationQueuePriority: 1,
    currentAnimation: []
  }

	createGizmo = () => {
		this.setState({
			gizmoCount : this.state.gizmoCount + 1,
			gizmoArray: [...this.state.gizmoArray, <Gizmo key={this.state.gizmoCount} number={this.state.gizmoCount} onFilterOut={this.convertMonster} />]			
		});
	}

	convertMonster(monsterNumber) {
		//this.craneIn(monsterNumber);
		this.setState({
			animationQueue: [
			  {
			  	animationSequence: craneToPickup,
			  	priority: 1
			  },
			  {
			  	animationSequence: cranePickup,
			  	priority: 2
			  },
			  {
			  	c,
			  	priority: 2
			  },
			  {
			  	d,
			  	priority: 3
			  }
			],
			currentAnimation: []
		});
	}

	craneIn(monsterNumber) {
		console.log('craneIn');

		const element = document.getElementById('crane');

		// element.addEventListener('animationend', () => {
	 //      element.classList.add('crane-move-left');
	 //    });
		element.classList.add('crane-move-right');
	}

	pickUpMonster(monsterNumber) {
		//const element = document.getElementById('crane');
		//console.log(this.state.gizmoArray[monsterNumber]);
		//console.log(this.gizmoRef.current);
	}

	removeMonster(monsterNumber) {
		console.log('removeMonster');

		// run some animation here, wait till its done, and then fire set state like below.

		this.setState({
			gizmoArray : this.state.gizmoArray.filter(function(item) {
				return item.props.number !== monsterNumber
			})
		});
	}

	//current animation's done
	currentAnimationFinished(animation) {
		alert(animation);
		this.setState({
		  currentAnimation: []
		});
	}

	componentDidUpdate(prevProps, prevState) {

		//We have an updated animation queue
		if (prevState.animationQueue !== this.state.animationQueue) {
			console.log('We have an animation Queue!');

			let currentPriority = this.state.animationQueuePriority;

			let sendToCurrentAnimation = [];

			//while(this.state.animationQueue.length >= currentPriority) { 
			console.log('this.state.animationQueuePriority: ' + this.state.animationQueuePriority);
			console.log('is greater than or equal to currentPriority: ' + currentPriority);



			this.state.animationQueue.forEach(function(animation, index) { 
				console.log('Loop through every animation in the queue')
				if (animation.priority === currentPriority) {
					console.log('this is the same priority so add it to the current animation: ' + animation);
					sendToCurrentAnimation.push(animation);
				}				
				});
			currentPriority++

			console.log('this to current animation, we should be done: ' + sendToCurrentAnimation);
			this.setState({
			  currentAnimation: sendToCurrentAnimation,
			  animationQueuePriority: currentPriority
			});
			//}
		}		

		//we have current animation(s)
		if (prevState.currentAnimation !== this.state.currentAnimation && this.state.currentAnimation.length !== 0) {
			alert('here');
			console.log('prevstate: ' + prevState.currentAnimation);
			console.log('current: ' + this.state.currentAnimation);
				this.state.currentAnimation.forEach(function(animate, index) {
					animate.animationSequence(animate.animationSequence).then((result) => console.log('im happy?'));
				});

			}
		}


	render() {
		return (
		  <div className='gizmos-container'>
		  	<Crane />
		    <section className="gizmo-button-container">
		      <GizmoButton onClick={this.createGizmo} />
		    </section>
			  <section className="gizmo-creater">
				{this.state.gizmoArray}
			</section>
            <section className="gizmo-converter">
             <p>need to design a converter</p>
            </section>
            <section className="gizmo-result">
             <p>need to design a gizmo result</p>
            </section>
	      </div>
		)
	}
}


const craneToPickup = () => {
  const element = document.getElementById('crane');
  return (cssTransitionToPromise(element, 'margin-left', '38%')//.then((result) => currentAnimationFinished(result))
  )
}

