}

const c = () => {
	setTimeout(function() {
		alert('C');
	}, 2000);
}

const d = () => {
	setTimeout(function() {
		alert('D');
	}, 2000);
}

const e = () => {
	setTimeout(function() {
		alert('E');
		return('done')
	}, 2000);
}

const f = () => {
  setTimeout(function() {
		alert('F');
	}, 2000);
}

const g = () => {
  setTimeout(function() {
		alert('G');
	}, 2000);
}

const cssTransitionToPromise = (el, property, value) => {
return (
	  new Promise(resolve => {
      el.style[property] = value;
      const transitionEnded = e => {
      if (e.propertyName !== property) {
        console.log(e.propertyName);
    	return; //Wrong transition
      }
      else { 
    	el.removeEventListener('transitionend', transitionEnded);
  	    resolve('hi there!');
      }
    }
    el.addEventListener('transitionend', transitionEnded);
    })
  )
}

class GizmoButton extends React.Component {
  render() {

    return (
      <button onClick={this.props.onClick}>Make a Gizmo</button>
    ) 

  }
}
