import React from 'react';


let i = 0;	

const updateMonitor = (updateText) => {



	setTimeout(function () {
      document.getElementById('gizmo-updates-screen').append(updateText.charAt(i));    
      i++;
      if (i < updateText.length) {
         updateMonitor(updateText);
      }
   }, 30)

}

export default updateMonitor;
