import React from 'react';

export class Monster extends React.Component {

	constructor(props) {
		super(props);
		this.monsterRef = React.createRef();
	}

	componentDidMount() {
		console.log('componentWillMount');

		//

const onRemove = () => {
	this.props.onRemove('monsterArray', this.props.number);
}

	const setup = (fn) => {
		console.log('setup');
		this.monsterRef.current.addEventListener('transitionend', function() {
			//This is for when the transition has ended...
			onRemove();
		});
	  // Calls the function with any required arguments
	  return fn(this.monsterRef.current)
	}

// Callback gets arguments from the above call
	const moveOver = (element) => {
		window.setTimeout( function() {

	    element.className += ' monster-move';

	}, 50);

	}

// Passing a callback into a callback accepting function
setup(moveOver);



}

componentWillUnmount() {
	this.props.onCreateFloatingMonster(this.state, this.props);
}



	render() {
		return (
			<div ref={this.monsterRef} className={`monster ${this.props.species}`}>
				<div className='head' />
				<div className='torso' />
				<div className='left-arm' />
				<div className='right-arm' />
				<div className='left-leg' />
				<div className='right-leg' />
			</div>
		)
	}

}
